import React, { Component } from 'react';
import { Layer, Image, Group } from 'react-konva';


class Bg extends Component {
  state = { 
    bg: ''
   }
  componentDidMount(){
    console.log(this)
    const img1 = new window.Image();
    // img1.src = "http://3.bp.blogspot.com/-SUcep_O5m2k/VSu7OeMzRjI/AAAAAAAAAuc/Fg_owdvXj0U/s1600/mountainsday.png";
    img1.src = "http://www.sonyphotog.com/photos/globalmedicalco/10/49243.jpg";
    img1.onload = () => this.setState({bg:img1 ,img1});

    const img2 = new window.Image();
    img2.src = "https://verdict4u.files.wordpress.com/2016/09/google-now-wallpaper-2.png";
    
    img2.onload = () => this.setState({img2});

    const clouds = new window.Image();
    clouds.src = "http://clipartstation.com/wp-content/uploads/2018/09/clouds-clipart-transparent-background-2.png";
    clouds.onload = () => this.setState({clouds});

    setInterval(() => {
      const bg = this.state.bg === img1 ? img2: img1;
      this.setState({bg});
    }, 10000);
    let x = 0;
    setInterval(() => {
      x+=10;
      if(x > window.innerWidth){
        const clouds = this.clouds;
        clouds.hide();
        x=-100;
        clouds.to({x: x, duration: 2, onFinish: ()=> clouds.show()});
      }

      this.clouds.to({
        x: x
      })
    }, 100);

  }
  render() { 
    console.log(this)
    return ( 
      <Layer>
        <Image
        x={0}
        y={0}
        width={window.innerWidth}
        height={window.innerHeight}
        image={this.state.bg}
        ref={node => {
          this.imageNode = node;
        }}
        />
      <Group
        x={0}
        ref = {node => {
          this.clouds = node
        }}
      >
        <Image
          x={0}
          y={100}
          width={100}
          height={100}
          image={this.state.clouds}
        />
        <Image
          x={20}
          y={120}
          width={100}
          height={100}
          image={this.state.clouds}
        />
        <Image
          x={120}
          y={130}
          width={100}
          height={100}
          image={this.state.clouds}
        />
        <Image
          x={220}
          y={100}
          width={100}
          height={100}
          image={this.state.clouds}
        />
      </Group>
      </Layer>
     );
  }
}
 
export default Bg;